/* ==UserStyle==
@name NixOS Search Catppuccin
@namespace github.com/catppuccin/userstyles/styles/nixos-search
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/nixos-search
@version 0.4.1
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/nixos-search/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Anixos-search
@description Soothing pastel theme for NixOS Search
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("search.nixos.org") {
  @media (prefers-color-scheme: light) {
    :root {
      #catppuccin(@lightFlavor, @accentColor);
    }
  }

  @media (prefers-color-scheme: dark) {
    :root {
      #catppuccin(@darkFlavor, @accentColor);
    }
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    color-scheme: if(@lookup = latte, light, dark);

    ::selection {
      background-color: fade(@accent-color, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    --background-color: @base;
    --badge-background: @accent-color;
    --button-active-background: @surface1;
    --button-active-hover-background: @surface2;
    --button-background: @surface0;
    --button-hover-background: @surface2;
    --color-active-hover-tab: @surface1;
    --color-active-tab: @surface0;
    --color-hover-tab: @surface1;
    --headerbar-background-color: @mantle;
    --hover-background: @surface0;
    --link-color: @accent-color;
    --info-label-background: @accent-color;
    --dark-blue: @accent-color;
    --light-blue: @accent-color; // used by focus outline
    --line-color: @surface0;
    --search-result-short-details-color: @subtext1;
    --search-result-divider-line-color: @surface0;
    --search-result-title-color: @accent-color;
    --search-sidebar-link-color: @text;
    --search-sidebar-selected-link-background: @accent-color;
    --search-sidebar-selected-link-color: @crust;
    --terminal-background: @surface0;
    --terminal-color: @red;
    --text-color: @text;
    --text-color-light: @text;
    --text-color-warning: @yellow;

    // hardcoded to #fff
    .label,
    .badge {
      color: @crust;
    }

    // hardcoded to #005580
    a:hover,
    a:focus {
      color: @text;
    }
  }
}

/* prettier-ignore */
@catppuccin: {
  @latte:     { @rosewater: #dc8a78; @flamingo: #dd7878; @pink: #ea76cb; @mauve: #8839ef; @red: #d20f39; @maroon: #e64553; @peach: #fe640b; @yellow: #df8e1d; @green: #40a02b; @teal: #179299; @sky: #04a5e5; @sapphire: #209fb5; @blue: #1e66f5; @lavender: #7287fd; @text: #4c4f69; @subtext1: #5c5f77; @subtext0: #6c6f85; @overlay2: #7c7f93; @overlay1: #8c8fa1; @overlay0: #9ca0b0; @surface2: #acb0be; @surface1: #bcc0cc; @surface0: #ccd0da; @base: #eff1f5; @mantle: #e6e9ef; @crust: #dce0e8; };
  @frappe:    { @rosewater: #f2d5cf; @flamingo: #eebebe; @pink: #f4b8e4; @mauve: #ca9ee6; @red: #e78284; @maroon: #ea999c; @peach: #ef9f76; @yellow: #e5c890; @green: #a6d189; @teal: #81c8be; @sky: #99d1db; @sapphire: #85c1dc; @blue: #8caaee; @lavender: #babbf1; @text: #c6d0f5; @subtext1: #b5bfe2; @subtext0: #a5adce; @overlay2: #949cbb; @overlay1: #838ba7; @overlay0: #737994; @surface2: #626880; @surface1: #51576d; @surface0: #414559; @base: #303446; @mantle: #292c3c; @crust: #232634; };
  @macchiato: { @rosewater: #f4dbd6; @flamingo: #f0c6c6; @pink: #f5bde6; @mauve: #c6a0f6; @red: #ed8796; @maroon: #ee99a0; @peach: #f5a97f; @yellow: #eed49f; @green: #a6da95; @teal: #8bd5ca; @sky: #91d7e3; @sapphire: #7dc4e4; @blue: #8aadf4; @lavender: #b7bdf8; @text: #cad3f5; @subtext1: #b8c0e0; @subtext0: #a5adcb; @overlay2: #939ab7; @overlay1: #8087a2; @overlay0: #6e738d; @surface2: #5b6078; @surface1: #494d64; @surface0: #363a4f; @base: #24273a; @mantle: #1e2030; @crust: #181926; };
  @mocha:     { @rosewater: #f5e0dc; @flamingo: #f2cdcd; @pink: #f5c2e7; @mauve: #cba6f7; @red: #f38ba8; @maroon: #eba0ac; @peach: #fab387; @yellow: #f9e2af; @green: #a6e3a1; @teal: #94e2d5; @sky: #89dceb; @sapphire: #74c7ec; @blue: #89b4fa; @lavender: #b4befe; @text: #cdd6f4; @subtext1: #bac2de; @subtext0: #a6adc8; @overlay2: #9399b2; @overlay1: #7f849c; @overlay0: #6c7086; @surface2: #585b70; @surface1: #45475a; @surface0: #313244; @base: #1e1e2e; @mantle: #181825; @crust: #11111b; };
}

// vim:ft=less

/* ==UserStyle==
@name ChatGPT Catppuccin
@namespace github.com/catppuccin/userstyles/styles/chatgpt
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/chatgpt
@version 0.3.4
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/chatgpt/catppuccin.user.css
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Achatgpt
@description Soothing pastel theme for ChatGPT
@author Catppuccin
@license MIT

@preprocessor  less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("chatgpt.com") {
  @import url("https://unpkg.com/@catppuccin/highlightjs@1.0.0/css/catppuccin-variables.important.css");

  :root.light {
    #catppuccin(@lightFlavor, @accentColor);
  }

  :root.dark {
    #catppuccin(@darkFlavor, @accentColor);
  }

  #catppuccin(@lookup, @accent) {
    @rosewater: @catppuccin[@@lookup][@rosewater];
    @flamingo: @catppuccin[@@lookup][@flamingo];
    @pink: @catppuccin[@@lookup][@pink];
    @mauve: @catppuccin[@@lookup][@mauve];
    @red: @catppuccin[@@lookup][@red];
    @maroon: @catppuccin[@@lookup][@maroon];
    @peach: @catppuccin[@@lookup][@peach];
    @yellow: @catppuccin[@@lookup][@yellow];
    @green: @catppuccin[@@lookup][@green];
    @teal: @catppuccin[@@lookup][@teal];
    @sky: @catppuccin[@@lookup][@sky];
    @sapphire: @catppuccin[@@lookup][@sapphire];
    @blue: @catppuccin[@@lookup][@blue];
    @lavender: @catppuccin[@@lookup][@lavender];
    @text: @catppuccin[@@lookup][@text];
    @subtext1: @catppuccin[@@lookup][@subtext1];
    @subtext0: @catppuccin[@@lookup][@subtext0];
    @overlay2: @catppuccin[@@lookup][@overlay2];
    @overlay1: @catppuccin[@@lookup][@overlay1];
    @overlay0: @catppuccin[@@lookup][@overlay0];
    @surface2: @catppuccin[@@lookup][@surface2];
    @surface1: @catppuccin[@@lookup][@surface1];
    @surface0: @catppuccin[@@lookup][@surface0];
    @base: @catppuccin[@@lookup][@base];
    @mantle: @catppuccin[@@lookup][@mantle];
    @crust: @catppuccin[@@lookup][@crust];
    @accent-color: @catppuccin[@@lookup][@@accent];

    --ctp-rosewater: @rosewater;
    --ctp-flamingo: @flamingo;
    --ctp-pink: @pink;
    --ctp-mauve: @mauve;
    --ctp-red: @red;
    --ctp-maroon: @maroon;
    --ctp-peach: @peach;
    --ctp-yellow: @yellow;
    --ctp-green: @green;
    --ctp-teal: @teal;
    --ctp-sky: @sky;
    --ctp-sapphire: @sapphire;
    --ctp-blue: @blue;
    --ctp-lavender: @lavender;
    --ctp-text: @text;
    --ctp-subtext1: @subtext1;
    --ctp-subtext0: @subtext0;
    --ctp-overlay2: @overlay2;
    --ctp-overlay1: @overlay1;
    --ctp-overlay0: @overlay0;
    --ctp-surface2: @surface2;
    --ctp-surface1: @surface1;
    --ctp-surface0: @surface0;
    --ctp-base: @base;
    --ctp-mantle: @mantle;
    --ctp-crust: @crust;

    color-scheme: if(@lookup = latte, light, dark);

    ::selection {
      background-color: fade(@accent-color, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    code.hljs {
      background: none !important;
    }
    pre {
      .bg-token-main-surface-secondary {
        background-color: @surface0;
      }
      .bg-gray-950 {
        background-color: @crust;
      }
      .text-token-text-secondary {
        color: @subtext0;
      }
    }

    .dark\:bg-gray-950:is(.dark *) {
      background-color: @mantle;
    }

    color: @text;

    @white: if(@lookup=latte, @crust, @text);
    @black: if(@lookup=latte, @text, @crust);
    --white: @white;
    --black: @black;

    & when not (@lookup = latte) {
      --gray-50: lighten(mix(@text, @subtext0), 5%);
      --gray-100: @text;
      --gray-200: @subtext1;
      --gray-300: @overlay2;
      --gray-400: @overlay0;
      --gray-500: @surface2;
      --gray-600: @surface1;
      --gray-700: @surface0;
      --gray-750: @surface0;
      --gray-800: @base;
      --gray-900: @mantle;
      --gray-950: @crust;
      --brand-purple: @accent-color;
      --text-primary: var(--gray-100) !important;
      --text-secondary: var(--gray-200) !important;
      --text-tertiary: var(--gray-300) !important;
      --text-quaternary: var(--gray-500) !important;
      --main-surface-primary: var(--gray-800) !important;
      --main-surface-secondary: var(--gray-700) !important;
      --main-surface-tertiary: var(--gray-600) !important;
      --sidebar-surface-primary: var(--gray-900);
      --sidebar-surface-secondary: var(--gray-800);
      --sidebar-surface-tertiary: var(--gray-700);
      .popover,
      .dark .popover,
      .dark.popover,
      .popover .dark {
        --main-surface-primary: var(--gray-700) !important;
        --main-surface-secondary: var(--gray-600) !important;
        --main-surface-tertiary: var(--gray-500) !important;
        --text-primary: var(--white) !important;
        --text-secondary: var(--gray-200) !important;
        --text-tertiary: var(--gray-300) !important;
      }
    }
    & when (@lookup = latte) {
      --gray-50: @crust;
      --gray-100: @mantle;
      --gray-200: @base;
      --gray-300: @surface0;
      --gray-400: @surface1;
      --gray-500: @surface2;
      --gray-600: @overlay0;
      --gray-700: @overlay1;
      --gray-800: @subtext0;
      --gray-900: @subtext1;
      --gray-950: @text;
      --brand-purple: @accent-color;
      --text-primary: var(--gray-950);
      --text-secondary: var(--gray-600);
      --text-tertiary: var(--gray-400);
      --text-quaternary: var(--gray-300);
      --main-surface-primary: var(--gray-200);
      --main-surface-secondary: var(--gray-100);
      --main-surface-tertiary: var(--gray-50);
      --sidebar-surface-primary: var(--gray-100);
      --sidebar-surface-secondary: var(--gray-200);
      --sidebar-surface-tertiary: var(--gray-300);
      .popover,
      .dark .popover,
      .dark.popover,
      .popover .dark {
        --main-surface-primary: var(--gray-200) !important;
        --main-surface-secondary: var(--gray-100) !important;
        --main-surface-tertiary: var(--gray-50) !important;
        --text-primary: var(--gray-950) !important;
        --text-secondary: var(--gray-600) !important;
        --text-tertiary: var(--gray-500) !important;
      }
    }

    --border-light: fade(@text, 10%);
    --border-medium: fade(@text, 15%);
    --border-heavy: fade(@text, 20%);
    --border-xheavy: fade(@text, 25%);
    --link: @accent-color;
    --link-hover: if(
      @lookup = latte,
      lighten(@accent-color, 10%),
      darken(@accent-color, 10%)
    );

    *:focus {
      --tw-ring-offset-color: @base;
      --tw-ring-color: @accent-color;
    }

    /* ChatGPT logo */
    [style*="background-color: rgb(25, 195, 125);"] {
      background-color: @green !important;
      color: @base !important;
    }

    /* Input */

    select {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 20"><path stroke="@{overlay2}" stroke-linecap="round" stroke-linejoin="round" stroke-width="1.5" d="m6 8 4 4 4-4"/></svg>'
      );
      background-image: url("data:image/svg+xml;@{svg}");
    }

    [multiple],
    [type="date"],
    [type="datetime-local"],
    [type="email"],
    [type="month"],
    [type="number"],
    [type="password"],
    [type="search"],
    [type="tel"],
    [type="text"],
    [type="time"],
    [type="url"],
    [type="week"],
    select,
    textarea {
      background-color: @base;
      border-color: @overlay2;
    }

    [type="checkbox"],
    [type="radio"] {
      background-color: @base;
      border-color: @overlay2;
      color: @accent-color;
    }

    .form-input,
    .form-multiselect,
    .form-select,
    .form-textarea {
      background-color: @base;
      border-color: @overlay2;
    }

    .form-input:focus,
    .form-multiselect:focus,
    .form-select:focus,
    .form-textarea:focus {
      border-color: @accent-color;
    }

    .form-input::placeholder,
    .form-textarea::placeholder {
      color: @overlay2;
    }

    /* Buttons */

    .btn-primary,
    .btn-green {
      background-color: @accent-color;
      color: @base;

      &:hover {
        background-color: darken(@accent-color, 10%);
      }

      &:focus-visible {
        --tw-ring-color: darken(@accent-color, 10%) !important;
      }
    }

    .btn-danger {
      background-color: @red;
      color: @base;

      &:hover:not(:disabled) {
        background-color: darken(@red, 10%);
      }

      &.focus-visible,
      &:focus-visible {
        --tw-ring-color: darken(@red, 10%) !important;
      }
    }

    .btn-danger-outline {
      border-color: @red;
      color: @red;

      &.focus-visible,
      &:focus-visible {
        --tw-ring-color: lighten(@red, 10%) !important;
      }
    }

    .btn-neutral {
      &.focus-visible,
      &:focus-visible {
        --tw-ring-color: @overlay0 !important;
      }
    }

    .btn-dark {
      background-color: @surface0;
      border-color: @overlay0;
      color: @text;

      &:hover {
        background-color: @surface1;
      }
    }

    .btn-light {
      background-color: @overlay1;
      color: @black;

      &:hover {
        background-color: @overlay0;
      }
    }

    .btn-disabled,
    .btn-disabled:hover {
      background-color: @text;
    }

    /* Borders */

    .border-black,
    .border-black\/10,
    .border-black\/20,
    .border-black\/30,
    .border-black\/5 {
      border-color: var(--border-light);
    }

    .border-gray-100 {
      border-color: var(--gray-100);
    }

    .border-gray-200 {
      border-color: var(--gray-200);
    }

    .border-gray-300 {
      border-color: var(--gray-300);
    }

    .border-gray-400 {
      border-color: var(--gray-400);
    }

    .border-gray-500 {
      border-color: var(--gray-500);
    }

    .border-gray-700 {
      border-color: var(--gray-700);
    }

    .border-gray-950 {
      border-color: var(--gray-950);
    }

    .border-green-500 {
      border-color: lighten(@green, 10%);
    }

    .border-green-600 {
      border-color: @green;
    }

    .border-orange-400 {
      border-color: lighten(mix(@red, @yellow), 10%);
    }

    .border-orange-500 {
      border-color: mix(@red, @yellow);
    }

    .border-red-500 {
      border-color: darken(@red, 10%);
    }

    .border-white {
      border-color: @white;
    }

    .border-white\/20 {
      border-color: fade(@white, 20%);
    }

    .border-t-[\#0077FF] {
      border-top-color: @blue;
    }

    /* Backgrounds */
    .bg-token-main-surface-primary {
      background-color: var(--main-surface-primary);
    }

    .\!bg-brand-purple {
      background-color: @accent-color !important;
    }

    .\!bg-gray-200 {
      background-color: var(--gray-200) !important;
    }

    .btn-blue {
      background-color: @blue;
      color: @crust;

      &:hover {
        background-color: darken(@blue, 5%);
      }
    }

    .bg-[\#0077FF],
    .bg-\[\#3C46FF\],
    .bg-[\#3C46FF],
    .bg-[\#4046EC] {
      background-color: @blue;
    }

    .hover\:bg-\[\#0000FF\]:hover {
      background-color: darken(@blue, 5%);
    }

    .bg-[\#10A37F] {
      background-color: @green;
    }

    .bg-[\#B161FD] {
      background-color: @accent-color;
    }

    .bg-[\#F8CA27] {
      background-color: @yellow;
    }

    .bg-[\#FF5588] {
      background-color: mix(@red, @pink);
    }

    .bg-[\#FF6E3C] {
      background-color: saturate(mix(@red, @yellow), 10%);
    }
    .dark\:radix-highlighted\:bg-\[\#2E2F33\][data-highlighted] {
      background-color: @surface0;
    }

    .bg-black {
      background-color: @black;
    }

    .bg-black\/20 {
      background-color: fade(@black, 20%);
    }

    .bg-black\/5 {
      background-color: fade(@black, 5%);
    }

    .bg-black\/50 {
      background-color: fade(@black, 50%);
    }

    .bg-black\/90 {
      background-color: fade(@black, 90%);
    }

    .bg-black\/\[\.04\] {
      background-color: fade(@black, 4%);
    }

    .bg-black\/\[\.08\] {
      background-color: fade(@black, 8%);
    }

    .bg-black\/\[\.12\] {
      background-color: fade(@black, 12%);
    }

    .bg-blue-100 {
      background-color: lighten(@blue, 30%);
    }

    .bg-blue-200 {
      background-color: lighten(@blue, 20%);
    }

    .bg-blue-300 {
      background-color: lighten(@blue, 10%);
    }

    .bg-blue-400 {
      background-color: @blue;
    }

    .bg-blue-500 {
      background-color: darken(@blue, 5%);
    }

    .bg-blue-700 {
      background-color: darken(@blue, 10%);
    }

    .hover\:bg-blue-700:hover {
      background-color: darken(@blue, 15%);
    }

    .bg-brand-blue-800 {
      background-color: darken(@blue, 10%);
    }

    .bg-blue-950 {
      background-color: darken(@blue, 30%);
    }

    .checked\:bg-blue-600:checked {
      background-color: @accent-color;
    }

    .bg-brand-green {
      background-color: lighten(@green, 10%);
    }

    .bg-brand-green\/20 {
      background-color: fade(lighten(@green, 10%), 20%);
    }

    .bg-brand-green\/40 {
      background-color: fade(lighten(@green, 10%), 40%);
    }

    .bg-brand-purple {
      background-color: @accent-color;
    }

    .bg-gray-100 {
      background-color: var(--gray-100);
    }

    .bg-gray-200 {
      background-color: var(--gray-200);
    }

    .bg-gray-300 {
      background-color: var(--gray-300);
    }

    .bg-gray-400 {
