    }

    .wikitable {
      background-color: @base;
    }

    .wikitable > tr > th,
    .wikitable > tr > td,
    .wikitable > * > tr > th,
    .wikitable > * > tr > td {
      color: @text;
      border-color: @crust;
    }

    .wikitable > tr > th,
    .wikitable > * > tr > th {
