      border-color: @surface2 !important;
    }

    .oo-ui-menuSelectWidget {
      background-color: @surface0;
      border-color: @surface2;
    }

    .oo-ui-OptionWidget-highlighted {
      background-color: @overlay0 !important;
    }

    .oo-ui-menuOptionWidget.oo-ui-widget-enabled.oo-ui-optionWidget,
    .minoredit,
    .comment {
      color: @text;
    }

    .mw-footer-container {
      color: @text !important;
    }

    #archnavbarlist li a {
      color: @blue !important;
    }

    .anb-selected {
      color: @subtext1 !important;
    }

    .mw-footer li {
      color: @text;
    }

    .vector-main-menu,
    .vector-pinnable-element {
      background-color: @base !important;
    }

    #p-navigation ul li a {
      color: @blue !important;
    }

    .archwiki-template-box-note a {
      color: @blue !important;
    }

    .archwiki-template-box-note a:visited {
      color: @mauve !important;
    }

    .mw-page-title-main {
      color: @text;
    }

    .vector-tab-noicon mw-list-item a {
      color: @blue;
    }

    .mw-header {
      background-color: @base !important;
      color: @text;
    }

    .mw-footer li a {
      color: @blue;
    }

    .cdx-text-input__input:enabled {
      background-color: @surface0 !important;
      color: @text !important;
      border-color: @surface1;
    }

    .cdx-button:enabled,
    .cdx-button.cdxbutton--fake-button--enabled {
      color: @mantle;
      background-color: @accent-color;
      border-color: @base;
    }

    .cdx-menu {
      background-color: @surface0;
    }

    .cdx-menu:hover {
      background-color: @base !important;
    }

    .anb-home {
      color: @text !important;
    }

    .vector-dropdown-content {
      background-color: @base !important;
    }

    .mw-headline::before {
      color: @accent-color !important;
    }

    .cdx-button {
      background-color: @base !important;
      color: @text !important;
      border-color: @base !important;
    }

    .mw-body {
      background-color: @base !important;
      color: @text !important;
      border-color: @base !important;
    }

    .mw-search-profile-tabs {
      background-color: @base !important;
      color: @text !important;
      border-color: @base !important;
    }

    .mw-content-ltr {
      background-color: @base !important;
    }

    .vector-sticky-pinned-container::after {
      background: @base !important;
    }

    .cdx-search-input {
      border-color: @mantle !important;
    }

    .oo-ui-widget:hover {
      background-color: @surface0 !important;
    }
  }
}

