    }

    .talkheader-help {
      background-color: @surface1 !important;
      border-color: @green !important;
    }

    .assess,
    .assess-NA,
    .navbox-subgroup {
      background: @surface0 !important;
      border-color: @surface1 !important;
    }

    .documentation,
    .documentation-container,
    .documentation-metadata {
