    --dt-shadow-elevation-4: 0px 5px 5px -3px fade(@base, 20%),
      0px 8px 10px 1px fade(@base, 14%), 0px 3px 14px 2px fade(@base, 12%);
    --dt-shadow-elevation-5: 0px 8px 10px -6px fade(@base, 20%),
